import React, { useEffect } from 'react'
import SiteHeader from '../components/header/header';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { selectedProduct } from '../redux/actions/productsActions';

function BookPage() {
    const book=useSelector(state=>state.product);
    const dispatch=useDispatch()
    const url=process.env.REACT_APP_BASE_URL;
    const id=useParams().id;

    const fetchBook=async ()=>{
        const response=await axios.get(`${url}/book/${id}`).catch(e=>console.log(e));
        dispatch(selectedProduct(response.data));

    }


    useEffect(()=>{
        fetchBook();
    },[]);


  return (
    <div>
        <SiteHeader/>
        <div className='w-full h-[83vh] bg-black flex flex-row'>
            <div className='bg-[red] ml-5 h-[100%] w-[50%]'>
                <img src={book.image}></img>
            </div>
            <div className='bg-[red] ml-5 h-full w-[50%]'>HI</div>
        </div>
    </div>
  )
}

export default BookPage
